insert.Script:
-- Prevent System from updating . -- we will update manually.
if @EFFECTIVE_IND <> 'Y' begin
set @Result_ShowAlert = 'Current Content is already :' +@EFFECTIVE_IND ;
set @BusinessRules_PreventDefault = 1;
GOTO WRAPUP; end
-- Prevent System from updating . -- we will update manually. set @BusinessRules_PreventDefault = 1;
-- increment the content version first. update t_dm_content set content_ver = content_ver +1 where content_id = @CONTENT_ID and content_ver = @CONTENT_VER;
-- copy the record, before new changes apply. insert into t_dm_content (CUSTOMER_ID,CONTENT_ID,CONTENT_VER, CONTENT_DOC_TYPE, CONTENT_NAME, CONTENT_PATH, EFFECTIVE_IND, LAST_VERSION_IND, CTRL_USER_ID, CTRL_CRE_DATE, CTRL_UPD_DATE, CTRL_APP_NAME, SECURITY_PROF, NOTES) select customer_id, content_id, content_ver-1, content_doc_type, content_name, content_path, effective_ind, 'N', ctrl_user_id, CTRL_CRE_DATE, getdate(), null, null, notes from t_dm_content where content_id = @CONTENT_ID and content_ver = @CONTENT_VER+1 and last_version_ind = 'Y';
set @LAST_VERSION_IND = 'Y'; set @CONTENT_VER = @CONTENT_VER +1;
-- at this stage ContentVersion has already been incremented and inserted. update t_dm_content set last_version_ind = @LAST_VERSION_IND, effective_ind = 'N', ctrl_user_id = @UserName, ctrl_upd_date = getdate(), ctrl_cre_date = getdate() where content_id = @CONTENT_ID and content_ver = @CONTENT_VER and last_version_ind = 'Y';
WRAPUP:
END

// tell the client library to skip the execution of update, insert, or delete
    this.preventDefault();    

