update.Script:
DECLARE @ExistingPacketNo nvarchar(50) DECLARE @ExistingProductID int DECLARE @ExistingProductName nvarchar(100) DECLARE @ExistingPieces int DECLARE @ExistingCube decimal(18,3) DECLARE @ExistingArea decimal(18,3) DECLARE @ExistingLineal decimal(18,3) DECLARE @ExistingPrice decimal(18,2) DECLARE @ExistingUOM nvarchar(50) DECLARE @Success as bit DECLARE @PacketCount as int DECLARE @PreviouslyConsumedOn date BEGIN SET @Success = 1 SET @Errors = null SELECT @PacketCount = count(*), @PreviouslyConsumedOn = min(ConsumedOn) FROM tConsignmentUsage WHERE tConsignmentUsage.CustomerID = @CustomerID and tConsignmentUsage.PacketNo = @PacketNo and tConsignmentUsage.ConsignmentUsageID <> ISNULL(@ConsignmentUsageID,0)
IF @PacketCount > 0 BEGIN SET @ProductID = null SET @ProductName = null SET @Errors = 'Packet already consumed on ' + Convert(nvarchar(30), @PreviouslyConsumedOn,103) SET @InStock = 0 SET @Pieces = null SET @Cube = null SET @Area = null SET @Lineal = null SET @Price = null SET @PriceUom = null END ELSE BEGIN SELECT @ExistingPacketNo = PacketNo, @ExistingProductID = tConsignmentStock.ProductID, @ExistingProductName = ProductName, @ExistingPieces = Pieces, @ExistingCube = Cube, @ExistingArea = Area, @ExistingLineal = Lineal, @ExistingPrice = Price, @ExistingUOM = PriceUOM FROM tConsignmentStock INNER JOIN tProduct ON tProduct.ProductID = tConsignmentStock.ProductID WHERE tConsignmentStock.CustomerID = @CustomerID and tConsignmentStock.PacketNo = @PacketNo
IF @ExistingPacketNo IS NULL BEGIN SET @ProductID = null SET @ProductName = null SET @Errors = 'Packet is not on consignment' SET @InStock = 0 SET @Pieces = null SET @Cube = null SET @Area = null SET @Lineal = null SET @Price = null SET @PriceUom = null END ELSE BEGIN SET @ProductID = @ExistingProductID SET @ProductName = @ExistingProductName SET @InStock = 1 SET @Errors = null SET @Pieces = @ExistingPieces SET @Cube = @ExistingCube SET @Area = @ExistingArea SET @Lineal = @ExistingLineal SET @Price = @ExistingPrice SET @PriceUom = @ExistingUOM
END END
INSERT INTO tConsignmentUsage ([CustomerID] ,[PacketNo] ,[ProductID] ,[ConsumedOn] ,[Pieces] ,[Cube] ,[Area] ,[Lineal] ,[Price] ,[PriceUOM] ,[InStock] ,[Errors] ,[SentOn] ,[CreatedBy] ,[CreatedOn] ,[ModifiedBy] ,[ModifiedOn]) SELECT @CustomerID, @PacketNo, @ProductID, @ConsumedOn, @Pieces, @Cube, @Area, @Lineal, @Price, @PriceUom, @InStock, @Errors, null, @BusinessRules_UserName, GetDate(), @BusinessRules_UserName, GetDate();
/* SET @LastPacketNo = @PacketNo; SET @LastError = @Errors; SET @LastProductName = @ProductName; SET @PacketNo = null SET @Errors = null SET @InStock = 0 SET @ProductID = null SET @ProductName = null SET @Pieces = null SET @Cube = null SET @Area = null SET @Lineal = null SET @Price = null SET @PriceUom = null SET @ConsumedOn = GetDate() */
SET @Result_Focus = 'PacketNo';
END

